import org.apache.poi.xssf.usermodel.*
import org.apache.poi.xssf.usermodel.XSSFWorkbook

println "load test data file"
def projectPath = testRunner.testCase.testSuite.project.getPath()
File dummyFile = new File(projectPath)
def projectDir = dummyFile.getParent()

File dummyFile2 = new File(projectDir)
def filepath = dummyFile2.getCanonicalPath()+"//TestData.xlsx"
//def logfile = dummyFile2.getCanonicalPath()+"//APILog.xlsx"

println "read test data into properties"
def projectName = testRunner.testCase.testSuite.project.getName()
FileInputStream file = new FileInputStream(filepath);
XSSFWorkbook wb = new XSSFWorkbook(file);
XSSFSheet sh = wb.getSheet(projectName)

//FileInputStream file2 = new FileInputStream(logfile);
//XSSFWorkbook wb2 = new XSSFWorkbook(file2);
//XSSFSheet sh2 = wb2.getSheet(projectName)

int maxRow = sh.getPhysicalNumberOfRows()
int maxCol = sh.getRow(0).getLastCellNum()

String system = testRunner.testCase.testSuite.project.getPropertyValue("Prop_Project_System").toUpperCase()
println "current test cases are running in system: "+system
def index = ['DEV': 4, 'QUAL': 5, 'TEST': 6]
def column = index[system]

for (int i=1;i<maxRow;i++){
	//Get property level: Project / TestSuite / TestCase
	String level = sh.getRow(i).getCell(0).toString().toUpperCase()
	String propertyName = sh.getRow(i).getCell(3).toString()
	String suiteName, caseName
	String propertyValue = sh.getRow(i).getCell(column).toString()

	//if the values are not given, we used the values defined in DEV column
	if (propertyValue == "null"){
		propertyValue = sh.getRow(i).getCell(4).toString()
	}
	switch (level){
		case 'PROJECT':
		testRunner.testCase.testSuite.project.setPropertyValue(propertyName, propertyValue)
		log.info "Project"+propertyName+propertyValue
		break
		case 'SUITE':
		suiteName = sh.getRow(i).getCell(1).toString()
		testRunner.testCase.testSuite.project.testSuites[suiteName].setPropertyValue(propertyName, propertyValue)
		log.info "Suite"+propertyName+propertyValue
		break
		case 'CASE': 
		suiteName = sh.getRow(i).getCell(1).toString()
		caseName = sh.getRow(i).getCell(2).toString()
		testRunner.testCase.testSuite.project.testSuites[suiteName].testCases[caseName].setPropertyValue(propertyName, propertyValue)
		log.info "Case : "+propertyName+propertyValue
		break
	}

}

