con:config.script:
def properties = new com.eviware.soapui.support.types.StringToObjectMap ()
//def step_CheckLogon_Result = testRunner.runTestStepByName(context.testCase.testStepList[context.currentStepIndex - 1].name)
def step_CheckLogon_Result = testRunner.runTestStepByName("Check Logon")
def LogOnStatus = step_CheckLogon_Result.status.toString()
//log.info context.testCase.testStepList[context.currentStepIndex - 1].name
log.info LogOnStatus
def rerunCount=1
def MaxCount=3
//testRunner.getStatus().toString()
if (LogOnStatus=='OK')
{
	log.info LogOnStatus
	}
else
{
	while(rerunCount<=MaxCount)
	{
	log.info "-----This is the "+rerunCount+" time to rerun the logon-----"
	for (int n=0;n<=10;n++)
	{
		testRunner.runTestStepByName(context.testCase.testStepList[n].name)
		log.info "This is rerun the step "+n+"   "+context.testCase.testStepList[n].name
		//log.info context.testCase.testStepList[n].name
		testRunner.runTestStepByName(context.testCase.testStepList[n].name)
		}
	def rerunChecklogOnstatus=testRunner.runTestStepByName(context.testCase.testStepList[10].name).status.toString()
	log.info "The " +rerunCount+" time to rerun the logOn result is "+rerunChecklogOnstatus
	rerunCount++
	if (rerunChecklogOnstatus=='OK')
	{
		break;
		}
	}
}

con:testCase.con:tearDownScript:
testRunner.testCase.testSuite.setPropertyValue("LogOnStatus", testRunner.getStatus().toString())

int retryAttempts = testRunner.testCase.testSuite.getPropertyValue("ReRunCount").toInteger()
while(testRunner.getStatus().toString() == "FAILED" && retryAttempts <= 1)
{
	retryAttempts++
	testCase.testSuite.setPropertyValue("ReRunCount", retryAttempts.toString())
	runner = testCase.run(new com.eviware.soapui.support.types.StringToObjectMap(), false)
	if (testRunner.getStatus().toString()=='FINISHED')		
		{
			break
			testCase.testSuite.setPropertyValue("LogOnStatus", testRunner.getStatus().toString())
		}
}
testRunner.testCase.testSuite.setPropertyValue("ReRunCount", "0")
testRunner.testCase.testSuite.setPropertyValue("LogOnStatus", testRunner.getStatus().toString())

