con:config.script:
def today = (new Date().format("yyyy-MM-dd HH:mm:ss"))
subject = "SNOW Inbound API(C) created at "+ today+" for integration testing_frontrunner case-dev instance."
testRunner.testCase.setPropertyValue("Prop_Case_Subject", subject);

def number = (new Date().format("yyMMddHHmm"))
def pointer = "0020751252"+number+"2018"
testRunner.testCase.setPropertyValue("Prop_Case_Pointer", pointer);

con:configuration.scriptText:
import groovy.json.JsonSlurper
def httpResponse = messageExchange.response.responseContent
def result = (new JsonSlurper().parseText(httpResponse)).result
assert  result.size() == 2


 for(record in result) {

	if(record.transform_map == "Case API for Postbox") 
	{
		assert record.status == "ignored"
		}
	else if (record.transform_map == "Case API for Launchpad")
	{
		assert record.status == "inserted"
		}

      }

