con:config.script:
def today = (new Date().format("yyyy-MM-dd HH:mm:ss"))
subject = "DO NOT CHANGE!  This case is update on "+ today+" by Calling Postbox API(C)"
testRunner.testCase.setPropertyValue("Prop_Case_Subject", subject);


// set priority
def priority,newpriority
priority=testRunner.testCase.getPropertyValue("Prop_Case_Priority");

	if (priority=="4")
	{newpriority="3"}
	else if (priority=="3")
	{newpriority="4"	}
	else
	{newpriority="3"}
testRunner.testCase.setPropertyValue("Prop_Case_Priority", newpriority);

con:configuration.scriptText:
def responseHeader = messageExchange.response.getResponseHeaders()

def token
responseHeader.each{
    if(it.key == "x-csrf-token")
        token = it.value
    }

messageExchange.modelItem.testStep.testCase.setPropertyValue("Prop_Case_Token",token[0])
log.info token[0]

