//get the response of testSteps
import com.eviware.soapui.support.XmlHolder

def responseXml = messageExchange.modelItem.testCase.testSteps["SearchTerm_hana"].testRequest.response.contentAsString;
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )  
def response = context.expand(responseXml)  
def holder = groovyUtils.getXmlHolder(response)

def NotesNumber = holder.getNodeValues("//*:Number"); 
def NotesTitle = holder.getNodeValues("//*:Title");

def system = messageExchange.modelItem.testCase.project.getPropertyValue("Prop_Project_System");

if(system.toLowerCase() != null && system.toLowerCase() == "dev") {
	log.info("Kumara dev system");
	assert NotesNumber[0] == "2112604" : "Notes number is '2112604'";
	assert NotesTitle[0] == "FAQ: SAP HANA Compression" : "Notes title contains 'FAQ: SAP HANA Compression'";
} else if (system.toLowerCase() != null && system.toLowerCase() == "qual") {
	log.info("Kumara qual system");
	assert NotesNumber[0] == "1920806" : "Notes number is '1920806'";
	assert NotesTitle[0] == "HANA: How to create models with lowercase name" : "Notes title contains 'HANA: How to create models with lowercase name'";
}

