def NotesNumber = holder.getNodeValues("//*:Number"); 
def NotesTitle = holder.getNodeValues("//*:Title");

def system = messageExchange.modelItem.testCase.project.getPropertyValue("Prop_Project_System");

if(system.toLowerCase() != null && system.toLowerCase() == "dev") {
	log.info("Kumara dev system");
	assert NotesNumber[1] == "1038917" : "Notes number is '1038917'";
	assert NotesTitle[1] == "How to set a selection formula for a report using the CE RAS 9 Java Viewer SDK" : "Notes title contains 'How to set a selection formula for a report using the CE RAS 9 Java Viewer SDK'";
