con:configuration.scriptText:
def responseHeader = messageExchange.response.getResponseHeaders()

def token
responseHeader.each{
    if(it.key == "x-csrf-token")
        token = it.value
    }

messageExchange.modelItem.testStep.testCase.setPropertyValue("Prop_Case_Token",token[0])

//get the response of testSteps
def responseXml = messageExchange.modelItem.testCase.testSteps["Filter1"].testRequest.response.contentAsString;
log.info("Kumara count:" + responseXml);
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )  
def response = context.expand(responseXml)  
log.info("kumara"+response)
def holder = groovyUtils.getXmlHolder(response)

def filterNoteNumber = holder.getNodeValues("//*:Number"); 
def filterNoteTitle = holder.getNodeValues("//*:Title");
def filterCategory = holder.getNodeValues("//*:Category"); 
def filterComponent = holder.getNodeValues("//*:Component");
def filterPriority = holder.getNodeValues("//*:Priority");

assert filterNoteNumber[13] == "2086480" : "Filter note number value contains '2086480'";
assert filterNoteTitle[13] == "Trace topics for the AFL framework" : "Filter note title value contains 'Trace topics for the AFL framework'";
assert filterCategory[13] == "FAQ" : "Filter category value should be 'FAQ'";
assert filterComponent[13] == "HAN-DB-AFL" : "Filter component value start with 'HAN-DB'";
assert filterPriority[13] == "Recommendations / Additional Info" : "Filter responsible value should be 'Recommendations / Additional Info'";

//get the response of testSteps
def responseXml = messageExchange.modelItem.testCase.testSteps["Filter2"].testRequest.response.contentAsString;
log.info("Kumara count:" + responseXml);
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )  
def response = context.expand(responseXml)  
log.info("kumara"+response)
def holder = groovyUtils.getXmlHolder(response)

def filterNoteNumber = holder.getNodeValues("//*:Number"); 
def filterNoteTitle = holder.getNodeValues("//*:Title");
def filterCategory = holder.getNodeValues("//*:Category"); 
def filterComponent = holder.getNodeValues("//*:Component");
def filterPriority = holder.getNodeValues("//*:Priority");

assert filterNoteNumber[7] == "2085087" : "Filter note number value contains '2085087'";
assert filterNoteTitle[7] == "CD: Secondary index on CDPOS_STR" : "Filter note title value contains 'CD: Secondary index on CDPOS_STR'";
assert filterCategory[7] == "Program error" : "Filter category value should be 'Program error'";
assert filterComponent[7] == "BC-SRV-ASF-CHD" : "Filter component value exclude 'HAN-DB'";
assert filterPriority[7] == "Correction with medium priority" : "Filter responsible value should be 'Correction with medium priority'";
