def responseXml = testRunner.testCase.testSteps["CombinationComponentFilter"].testRequest.response.contentAsString  
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )  
def response = context.expand(responseXml)  
log.info("kumara"+response)
def holder = groovyUtils.getXmlHolder(response) 
//def filterCountValue= holder.getNodeValue("//*:Count")  
def filterCountValue = holder.getNodeValues("//*:Count"); 
log.info("kumara count value:" + filterCountValue[0])
def filterSelectedValue = holder.getNodeValues("//*:Selected");
//log.info("kumara count value:" + filterSelectedValue[0])
def filterComponentValue = holder.getNodeValues("//*:Component");
log.info("kumara count value:" + filterComponentValue[0])
assert filterCountValue[0] == "8" : "Filter count value should be 8";
assert filterSelectedValue[0] == "true" : "Filter selected value should be true";
assert filterComponentValue[0] == "HAN*" : "Filter Component value should be ‘HAN*’";

assert filterCountValue[1] == "0" : "Filter count value should be 0";
assert filterSelectedValue[1] == "true" : "Filter selected value should be true";
assert filterComponentValue[1] == "BC-EIM-ESH" : "Filter Component value should be ‘BC-EIM-ESH’";

assert filterCountValue[2] == "8" : "Filter count value should be 8";
assert filterSelectedValue[2] == "false" : "Filter selected value should be false";
assert filterComponentValue[2] == "HAN-DP-LTR" : "Filter Component value should be ‘HAN-DP-LTR’";

