import groovy.json.JsonSlurper  

//get the response of testSteps
def xresponse = messageExchange.modelItem.testCase.testSteps["SearchTerm_hana"].testRequest.response.contentAsString
def slurper = new JsonSlurper()
def response = slurper.parseText(xresponse)
def count = response.resultCount;
def title = response.treeList.TITLE;
assert count == 183 : "Search result count value should be '183'";
assert title[0] == "HANA SSO Trace File Analysis" : "Search result contains 'hana'";
