//get the response of testSteps
def responseXml = messageExchange.modelItem.testCase.testSteps["PatchDayFilter"].testRequest.response.contentAsString;
log.info("Kumara count:" + responseXml);
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )  
def response = context.expand(responseXml)  
log.info("kumara"+response)
def holder = groovyUtils.getXmlHolder(response)

def filterPatchDayText = holder.getNodeValues("//*:PatchDayText"); 
def filterPatchDayCount = holder.getNodeValues("//*:Count");

assert filterPatchDayText[0] == "Support Package Security Notes" : "PatchDay filter text value should be 'Support Package Security Notes'";
assert filterPatchDayCount[0] == "5" : "PatchDay filter count value is '5'";

//get the response of testSteps
def responseXml = messageExchange.modelItem.testCase.testSteps["PriorityFilter"].testRequest.response.contentAsString;
