log.info("kumara"+response)
def holder = groovyUtils.getXmlHolder(response)

def filterPriorityText = holder.getNodeValues("//*:PriorityText"); 
def filterPPriorityCount = holder.getNodeValues("//*:Count");

assert filterPriorityText[0] == "Correction with medium priority" : "Priority filter text value should be 'Correction with medium priority'";
assert filterPPriorityCount[0] == "5" : "'Correction with medium priority' priority filter count value is '5'";

