def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )  
def response = context.expand(responseXml)  
log.info("kumara"+response)
def holder = groovyUtils.getXmlHolder(response)

def filterNumber = holder.getNodeValues("//*:Number"); 
def filterTitle = holder.getNodeValues("//*:Title"); 
def filterComponent = holder.getNodeValues("//*:Component");
def filterCategory = holder.getNodeValues("//*:Category"); 
def filterReleasedOn = holder.getNodeValues("//*:ReleasedOn");
def filterPatchDay = holder.getNodeValues("//*:PatchDay");
def filterPriority = holder.getNodeValues("//*:Priority");

assert filterNumber[0] == "1048911" : "Filter note number contains '1048911'";
assert filterTitle[0] == "Hard-coded credentials in [COMPONENTNAME] V3 is needed" : "'Filter note title is 'Hard-coded credentials in [COMPONENTNAME] V3 is needed'";
assert filterComponent[0] == "FI-AA" : "Filter Component value contains 'FI-AA'";
assert filterCategory[0] == "Program error" : "'Filter category value contains 'Program error'";
assert filterReleasedOn[0] == "2015-04-14T09:25:57" : "Filter note release on date is '2015-04-14T09:25:57'";
assert filterPatchDay[0] == "false" : "'Filter PatchDay value is 'false'";
assert filterPriority[0] == "Correction with medium priority" : "'Filter priority value is 'Correction with medium priority'";
