def testCase = context.getTestCase()
def testsuite = messageExchange.modelItem.testStep.testCase.testSuite
def expected = testsuite.getPropertyValue("AlreadyAct_INST")		
def response = messageExchange.responseContent
def root = new XmlSlurper().parseText(response)
def count = root.entry.size()
log.info ("count": count)
log.info ("expected": expected)
for (i = 0; i < count; i++){
def instnr = root.entry[i].content.properties.InstallationNumber
if (instnr == expected)
{ log.info ("active located in": i)
  def EuDP = root.entry[i].content.properties.EuDP
  assert EuDP == "A"
}
}

